mapper.select:
select locId locationId, locListId categoryId, locName locationName, locLat locationLat, locLng locationLng
from location
where locListId = #{categoryId};

select locId locationId, locListId categoryId, locName locationName, locLat locationLat, locLng locationLng
from location
where locRegionCode = #{regionCode};

select locId locationId, locListId categoryId, locName locationName, locAddr locationAddr, locRoadAddr locationRoadAddr, locTel locationTel
from location
where locId = #{locationId};
